// import P5Wrapper from "./utils/P5_Wrapper";

import { ReactP5Wrapper } from "react-p5-wrapper";

const sketch = (_p5) => {
  /*
  Made by: Floris Veldhuizen
  */

  let charpos, goalpos, leftpos, rightpos, leftfoot, rightfoot, speedvector, movevector, lastAngle = 0, charAngle = 0;
  let gpbool = false, mcbool = false, stepbool = false, stepboolcounter = 0;

  //player variables
  let charspeed = 1,
      maxspeed = 4,
      stepspeed = 25,
      drag = 0.9;

  // controller values
  let upPressed = 0, downPressed = 0, leftPressed = 0, rightPressed = 0;

  _p5.setup = () => {
    _p5.createCanvas(_p5.windowWidth, _p5.windowHeight);
    movevector = _p5.createVector(0,0);
    speedvector = _p5.createVector(0,0);
    goalpos = _p5.createVector(0,0);
    charpos = _p5.createVector( _p5.windowWidth/2, _p5.windowHeight/2);
    leftpos = _p5.createVector(charpos.x-20,charpos.y);
    rightpos = _p5.createVector(charpos.x+20,charpos.y);
    leftfoot = _p5.createVector(charpos.x-20,charpos.y);
    rightfoot = _p5.createVector(charpos.x+20,charpos.y);
  }

  _p5.draw = () => {
    _p5.background(255,255,255,255);
    if (mcbool) {
      _p5.ellipse(_p5.mouseX, _p5.mouseY, 40, 40);
      vectorControl();
    } else if (gpbool) {
      _p5.ellipse(goalpos.x, goalpos.y, 40, 40);
      vectorControl(goalpos.x,goalpos.y);
    }
    speedqualizervector();
    legReworked();
    lastAngle = charAngle;
    if(stepboolcounter > stepspeed){
      stepboolcounter = 0;
      stepbool = !stepbool;
    }
    stepboolcounter++;
  }

  function legReworked(){
      let curAngle = charAngle - lastAngle;
      rightpos.add(speedvector);
      leftpos.add(speedvector);
      leftpos = pointRotate(leftpos,charpos,curAngle);
      rightpos = pointRotate(rightpos,charpos,curAngle);

      if(!stepbool){
        leftfoot = _p5.p5.Vector.lerp(leftfoot,leftpos,0.1);
      } else {
        rightfoot = _p5.p5.Vector.lerp(rightfoot,rightpos,0.1);
      }

      // draw them leggos
      _p5.ellipse(leftfoot.x, leftfoot.y, 10, 10);
      _p5.ellipse(rightfoot.x, rightfoot.y, 10, 10);
  }

  function speedqualizervector(){
    speedvector.add(movevector.mult(charspeed));
    speedvector.limit(maxspeed);
    const speedvmagSq = speedvector.magSq();
    //if the player is not moving, the character should stop
    if (speedvmagSq > 0) {
      if(speedvmagSq.toFixed(2) > 0){
        if(movevector.x===0) speedvector.x *= drag;
        if(movevector.y===0) speedvector.y *= drag;
        charAngle = speedvector.heading() + _p5.HALF_PI;
      } else {
        speedvector.mult(0);
        charAngle = lastAngle;
      }
    }
    charpos.add(speedvector);
  }

  function setMovevector(){
    gpbool = false;
    movevector.mult(0);
    movevector.y += -1 * upPressed;
    movevector.y += 1 * downPressed;
    movevector.x += -1 * leftPressed;
    movevector.x += 1 * rightPressed;
    movevector.normalize();
  }

  function vectorControl(x = _p5.mouseX,y = _p5.mouseY){
    let vectorangle = _p5.createVector(0,0);
    vectorangle.x = (x - charpos.x);
    vectorangle.y = (y - charpos.y);
    if(vectorangle.magSq() > 15){
      movevector = vectorangle.normalize();
    } else {
      speedvector.mult(drag**2);
      movevector.mult(0);
      gpbool = false;
    }
  }

  /*
  KEY SHIT
  */
  _p5.keyPressed = () => {
    if (_p5.keyCode === _p5.LEFT_ARROW) {
      leftPressed = 1;
    }
    if (_p5.keyCode === _p5.RIGHT_ARROW) {
      rightPressed = 1;
    }
    if (_p5.keyCode === _p5.UP_ARROW) {
      upPressed = 1;
    }
    if (_p5.keyCode === _p5.DOWN_ARROW) {
      downPressed = 1;
    }
    setMovevector();
  }

  _p5.keyReleased = () => {
    if (_p5.keyCode === _p5.LEFT_ARROW) {
      leftPressed = 0;
    }
    if (_p5.keyCode === _p5.RIGHT_ARROW) {
      rightPressed = 0;
    }
    if (_p5.keyCode === _p5.UP_ARROW) {
      upPressed = 0;
    }
    if (_p5.keyCode === _p5.DOWN_ARROW) {
      downPressed = 0;
    }
    setMovevector();
  }
  _p5.mousePressed = () => {
    mcbool = true;
    gpbool = false;
  }
  _p5.mouseReleased = () => {
    mcbool = false;
    gpbool = true;
    goalpos.x = _p5.mouseX;
    goalpos.y = _p5.mouseY;
    movevector.x = 0;
    movevector.y = 0;
  }
  _p5.windowResized = () => {
    _p5.resizeCanvas(_p5.windowWidth, _p5.windowHeight);
  }

  /**
  * For lack of better rotation in p5.js
  * @param {vector} p5.Vector  	This is the vector (coord) that you want to rotate
  * @param {pivot}  p5.Vector  	This is the vector (coord) you want to pivot around
  * @param {angle}	number  		This is the angle of the rotation in radians
  */
  function pointRotate(vector,pivot,angle){
    const s = _p5.sin(angle);
    const c = _p5.cos(angle);
    // translate point back to origin:
    vector.sub(pivot);
    // rotate point
    let xnew = vector.x * c - vector.y * s;
    let ynew = vector.x * s + vector.y * c;
    // translate point back:
    vector.x = xnew + pivot.x;
    vector.y = ynew + pivot.y;
    return vector;
  }
}

const Walking = () => {
  return (
    <ReactP5Wrapper sketch={sketch} />
  );
};

export default Walking;
