import { ReactP5Wrapper } from "react-p5-wrapper";

const sketch = (p5) => {
  /*
  Made by: Floris Veldhuizen
  */

  let raindrops = [];
  let gravity = 0.1;
  let terminalvelocity = 20;
  let rainy = 180;
  let mousesize = 100;

  p5.setup = () => {
    p5.createCanvas(p5.windowWidth, p5.windowHeight);
    for (let i = 0; i < rainy; i++) {
      raindrops.push(new Drop());
    }
  }

  p5.draw = () => {
    p5.background(0,0,0,100);
    for (let i = 0; i < raindrops.length; i++) {
      raindrops[i].wind();
      raindrops[i].fall();
      raindrops[i].show();
    }
  }

  class Drop {
    constructor() {
      this.x = p5.random(p5.width);
      this.y = p5.random(-10, -p5.height);
      this.speed = p5.random(0, 2);
      this.falling = true;
      this.splash = false;
      this.splashlocx = 0;
      this.splashlocy = p5.height;
      this.splashprocess = 0;
      this.windpower = 10;

      this.show = function () {
        if (this.splash) {
          p5.stroke(255);
          p5.line(this.splashlocx, this.splashlocy, this.splashlocx - p5.random(4, 11), this.splashlocy - p5.random(5, 10));
          p5.line(this.splashlocx, this.splashlocy, this.splashlocx, this.splashlocy - p5.random(10, 16));
          p5.line(this.splashlocx, this.splashlocy, this.splashlocx + p5.random(4, 11), this.splashlocy - p5.random(5, 10));
          p5.fill(p5.color(0));
          p5.noStroke();
          p5.circle(this.splashlocx, this.splashlocy, this.splashprocess);
          this.splashprocess++;
          if (this.splashprocess > 16) {
            this.splash = false;
            this.falling = true;
            this.splashprocess = 0;
          }
        }
        if (this.falling) {
          p5.stroke(255);
          p5.line(this.x, this.y, this.x + this.windpower, this.y + this.speed);
        }
      };

      this.fall = function () {
        if (this.falling) {
          this.x = this.x + this.windpower;
          this.y = this.y + this.speed;
          this.speed = this.speed + gravity;
          if (this.y > p5.height) {
            resetRain(this, p5.height);
          }
          else if (collisionRain(this.x, this.y)) {
            resetRain(this, this.y);
          }
          if (this.x > p5.width) {
            this.x = 0;
          }
          if (this.x < 0) {
            this.x = p5.width;
          }
        }
      };

      this.wind = function () {
        this.windpower = -(p5.width / 2 - p5.mouseX) / 100;
      };
    }
  }

function collisionRain(x,y) {
	if(x > p5.mouseX - (mousesize/2) && x < p5.mouseX + (mousesize/2) && y > p5.mouseY && y < p5.mouseY + mousesize) {
		return true; //if hit
	} else {
		return false;
	}
}

function resetRain(that,splashlocy){
	that.splash = true;
	that.falling = false;
	that.splashlocy = splashlocy;
	that.splashlocx = that.x;
	that.x = p5.random(p5.width);
	that.y = -10;
	if(that.speed > terminalvelocity)
		that.speed = terminalvelocity;
}

p5.windowResized = () => {
  p5.resizeCanvas(p5.windowWidth, p5.windowHeight);
	raindrops = [];
	for (let i = 0; i < rainy; i++) {
		 raindrops.push(new Drop());
	}
}
}

const Rain = () => {
  return (
    <ReactP5Wrapper sketch={sketch} />
  );
};

export default Rain;
