import { useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { routes } from "../routes";

const Home = () => {
  const slothRef = useRef();
  // TO-DO: clean up this stuff a bit
  useEffect(() => {
    let patience = 0;
    let growth = 20;
    let sloth = slothRef.current;
    sloth.addEventListener("click",stoptouching);
    const responses = [
      "Ouch, stop pressing me!",
      "Hey, I warned you, quit it...",
      "Alright, now I am getting angry!",
      "I am going to get you!"
    ]
    const movethatsloth = () => {
      sloth.style.top = Math.floor(Math.random() * window.innerHeight)-(patience*growth);
      sloth.style.right = Math.floor(Math.random() * window.innerWidth)-(patience*growth);
    }
    const stoptouching = function(e){
      movethatsloth();
      if(patience < 2){
        alert(responses[patience]);
      } else if (patience < 4){
        sloth.classList.remove("anger");
        void sloth.offsetWidth; //weird CSS trick to restart keyframe animation
        sloth.classList.add("anger");
        sloth.style.fontSize = patience * growth + "px";
      } else if (false) {
        ///
      }
      if(patience > responses.length-1) {
        sloth.classList.add("shaking");
        alert(responses[responses.length-1]);
      } else {
        alert(responses[patience]);
      }
      patience++;
    }
  })

  return (
    <>
      <div className="container">
        <h1> Random projects</h1>
        <ol>
          <li><a href="fractals/index.html"> Mandelbrot Fractal </a> <span className="right"> (Drag mouse and scroll to interact)</span></li>
          <li><a href="portfolio/index.html"> Lines </a> <span className="right">(Use arrow keys) </span></li>
          <li><Link to={routes.walking}> Footsteps </Link> <span className="right">(Use arrow keys or mouseclicks) </span></li>
          <li><a href="chesspath/index.html"> Prince Chazz </a> <span className="right">(Chess with a twist) </span></li>
          <li><a href="card_js/index.html"> Card JS </a></li>
          <li><Link to={routes.rain}> Rain </Link> <span className="right">(Move around your mouse) </span> </li>
          <li><a href="qr_scan/index.html"> QR code test </a> <span className="right">(Try holding a QR code in front of your webcam) </span></li>
          <li><a href="p5_frames/index.html"> Funky frames </a> <span className="right">Move the mouse, and press r, c, p and m </span></li>
          <li><Link to={routes.funkySnake}>Funky Snake </Link> <span className="right">(Use arrow keys) </span></li>
        </ol>
      </div>
      <h2 ref={slothRef} id="secretsloth"> &#x1F9A5;</h2>
    </>
  );
}

export default Home;
